c_Game.prototype.p_OnUpdate=function(){
	var t_1=this.m_gameState;
	if(t_1=="MENU"){
		this.m_menuArray[this.m_butIndex].p_SetActive(true);
		if(((bb_input_KeyHit(83))!=0) && this.m_butIndex<2){
			this.m_menuArray[this.m_butIndex].p_SetActive(false);
			this.m_butIndex+=1;
			this.m_menuArray[this.m_butIndex].p_SetActive(true);
		}
		if(((bb_input_KeyHit(87))!=0) && this.m_butIndex>0){
			this.m_menuArray[this.m_butIndex].p_SetActive(false);
			this.m_butIndex-=1;
			this.m_menuArray[this.m_butIndex].p_SetActive(true);
		}
		if((bb_input_KeyHit(32))!=0){
			if(this.m_menuArray[this.m_butIndex].p_GetAction()=="PLAY"){
				this.m_p1.p_Reset();
				bb_audio_StopMusic();
				bb_audio_PlayMusic("music/level1.mp3",1);
				bb_random_Seed=bb_app_Millisecs();
				this.m_Room1.p_Build();
				this.m_Room2.p_Build();
				this.m_Room3.p_Build();
			}
			this.m_gameState=this.m_menuArray[this.m_butIndex].p_GetAction();
			this.m_menuArray[this.m_butIndex].p_SetActive(false);
			this.m_butIndex=0;
		}
	}else{
		if(t_1=="OPTIONS"){
			var t_2=this.m_optIndex;
			if(t_2==0){
				if((bb_input_KeyHit(83))!=0){
					this.m_optIndex+=1;
				}
				this.m_effectsVol.p_updateVol();
			}else{
				if(t_2==1){
					if((bb_input_KeyHit(87))!=0){
						this.m_optIndex-=1;
					}
					if((bb_input_KeyHit(83))!=0){
						this.m_optIndex+=1;
					}
					this.m_musicVol.p_updateVol();
				}else{
					if(t_2==2){
						this.m_optionsArray[this.m_butIndex].p_SetActive(true);
						if((bb_input_KeyHit(32))!=0){
							this.m_optionsArray[this.m_butIndex].p_SetActive(false);
							this.m_gameState=this.m_optionsArray[this.m_butIndex].p_GetAction();
						}
						if((bb_input_KeyHit(87))!=0){
							this.m_optIndex-=1;
							this.m_optionsArray[this.m_butIndex].p_SetActive(false);
						}
					}
				}
			}
			bb_audio_SetMusicVolume(this.m_musicVol.p_getVol());
		}else{
			if(t_1=="PLAY"){
				bb_app_HideMouse();
				this.m_Room1.p_UpdateCurrentRoom(this.m_p1);
				this.m_p1.p_Update();
				this.m_p1.p_UpdateX();
				this.m_p1.p_UseItem1(this.m_p1);
				if(this.m_Room1.p_TileCollision(this.m_p1.p_GetX(),Math.floor(this.m_p1.p_GetY()))){
					this.m_p1.p_CollRespX();
				}
				this.m_p1.p_UpdateY();
				if(this.m_Room1.p_TileCollision(this.m_p1.p_GetX(),Math.floor(this.m_p1.p_GetY()))){
					this.m_p1.p_CollRespY();
				}
				if(this.m_Room1.p_DamageCollision(this.m_p1.p_GetX(),Math.floor(this.m_p1.p_GetY()))){
					this.m_p1.p_Hurt();
				}
				if(this.m_p1.p_GetX()<=0.0){
					this.m_Room1.p_MoveRoom("EAST");
					this.m_p1.p_SetX(400.0);
				}
				if(this.m_p1.p_GetX()>=440.0){
					this.m_Room1.p_MoveRoom("WEST");
					this.m_p1.p_SetX(50.0);
				}
				if(this.m_p1.p_GetY()<=0.0){
					this.m_Room1.p_MoveRoom("NORTH");
					this.m_p1.p_SetY(400.0);
				}
				if(this.m_p1.p_GetY()>=440.0){
					this.m_Room1.p_MoveRoom("SOUTH");
					this.m_p1.p_SetY(50.0);
				}
				if((bb_input_KeyHit(70))!=0){
					this.m_Room1.p_InteractCurrentRoom(this.m_p1);
				}
				this.m_p1.p_Attack(this.m_Room1.p_GetCurrentRoomEnemies());
				if((bb_input_KeyHit(27))!=0){
					this.m_Room1.p_Reset();
					this.m_Room2.p_Reset();
					this.m_Room3.p_Reset();
					this.m_butIndex=0;
					this.m_gameState="MENU";
				}
				if(this.m_p1.p_GetCurHealth()==0){
					this.m_butIndex=0;
					bb_audio_StopMusic();
					this.m_gameState="DEAD";
				}
			}else{
				if(t_1=="DEAD"){
					this.m_deadArray[this.m_butIndex].p_SetActive(true);
					if((bb_input_KeyHit(32))!=0){
						this.m_deadArray[this.m_butIndex].p_SetActive(false);
						this.m_gameState=this.m_deadArray[this.m_butIndex].p_GetAction();
						bb_audio_PlayMusic("menus/menuMusic.wav",1);
					}
				}else{
					if(t_1=="EXIT"){
						bb_app_EndApp();
					}
				}
			}
		}
	}
	return 0;
}
c_Game.prototype.p_OnRender=function(){
	bb_graphics_Cls(0.0,0.0,0.0);
	var t_3=this.m_gameState;
	if(t_3=="MENU"){
		bb_graphics_DrawImage(this.m_menuBackground,0.0,0.0,0);
		this.m_playBut.p_Draw(160,225);
		this.m_optionsBut.p_Draw(160,300);
		this.m_exitBut.p_Draw(160,375);
	}else{
		if(t_3=="OPTIONS"){
			bb_graphics_DrawImage(this.m_menuBackground,0.0,0.0,0);
			this.m_effectsVol.p_Draw2();
			this.m_musicVol.p_Draw2();
			this.m_menuBut.p_Draw(160,375);
		}else{
			if(t_3=="PLAY"){
				this.m_Room1.p_DrawRoomFloor();
				this.m_p1.p_Draw2();
				this.m_Room1.p_DrawRoomWalls();
				this.m_Room1.p_DrawMap();
				bb_graphics_DrawText("Press ESC to reset",500.0,255.0,0.0,0.0);
				bb_graphics_DrawText("Seed:",500.0,300.0,0.0,0.0);
				bb_graphics_DrawText(String(bb_random_Seed),545.0,300.0,0.0,0.0);
				this.m_xhair.p_Draw2();
			}else{
				if(t_3=="DEAD"){
					this.m_menuBut.p_Draw(160,375);
				}
			}
		}
	}
	return 0;
}
