c_Room.prototype.p_UpdateRoom=function(t_player){
	var t_=this.m_enemyList.p_ObjectEnumerator();
	while(t_.p_HasNext()){
		var t_i=t_.p_NextObject();
		t_i.p_CheckActive(t_player.p_GetCentreX(),t_player.p_GetCentreY());
		t_i.p_CalcMove(t_player.p_GetCentreX(),t_player.p_GetCentreY());
		t_i.p_MoveX();
		if(bb_Enemies_CheckCollision(t_i,this.m_enemyList)==true){
			t_i.p_CorrectX();
		}
		if(bb_BasicLibrary_Collide(((t_player.p_GetX())|0),((t_player.p_GetY())|0),t_player.p_GetWidth(),t_player.p_GetHeight(),t_i.p_GetX(),t_i.p_GetY(),t_i.p_GetWidth(),t_i.p_GetHeight())==true){
			t_i.p_CorrectX();
		}
		t_i.p_MoveY();
		if(bb_Enemies_CheckCollision(t_i,this.m_enemyList)==true){
			t_i.p_CorrectY();
		}
		if(bb_BasicLibrary_Collide(((t_player.p_GetX())|0),((t_player.p_GetY())|0),t_player.p_GetWidth(),t_player.p_GetHeight(),t_i.p_GetX(),t_i.p_GetY(),t_i.p_GetWidth(),t_i.p_GetHeight())==true){
			t_i.p_CorrectY();
		}
		if(t_i.p_GetType()!=2){
			t_i.p_Attack3(t_player);
		}else{
			if(t_i.p_Summon(t_player)==true && this.m_enemyCount<10){
				this.m_enemyList.p_AddLast3(c_Enemy.m_new.call(new c_Enemy,4,(t_i.p_GetX()-30),(t_i.p_GetY()),true));
				this.m_enemyList.p_AddLast3(c_Enemy.m_new.call(new c_Enemy,4,(t_i.p_GetX()+30),(t_i.p_GetY()),true));
				this.m_enemyList.p_AddLast3(c_Enemy.m_new.call(new c_Enemy,4,(t_i.p_GetX()),(t_i.p_GetY()+30),true));
				this.m_enemyCount+=3;
			}
		}
		if(t_i.p_CheckHealth()==0){
			if(t_player.p_GetCurHealth()<3 && t_i.p_GetType()!=4){
				var t_chance=bb_random_Rnd();
				if(t_chance>0.6){
					var t_hp=c_HealthPotion.m_new.call(new c_HealthPotion,(t_i.p_GetX()),(t_i.p_GetY()));
					this.m_droppedItems.p_AddLast(t_hp);
				}
			}
			this.m_enemyList.p_Remove2(t_i);
			this.m_enemyCount-=1;
		}
	}
	if(this.m_enemyCount==0){
		this.m_complete=true;
	}
	if(this.m_complete==true){
		if(this.m_wDoor=="1"){
			this.m_CollisionArray[7][0]=0;
			this.m_WallLayout[7][0]=8;
			this.m_FloorLayout[7][0]=1;
		}
		if(this.m_eDoor=="1"){
			this.m_CollisionArray[7][14]=0;
			this.m_WallLayout[7][14]=8;
			this.m_FloorLayout[7][14]=1;
		}
		if(this.m_nDoor=="1"){
			this.m_CollisionArray[0][7]=0;
			this.m_CollisionArray[1][7]=0;
			this.m_WallLayout[0][7]=8;
			this.m_WallLayout[1][7]=8;
			this.m_FloorLayout[0][7]=1;
			this.m_FloorLayout[1][7]=1;
		}
		if(this.m_sDoor=="1"){
			this.m_CollisionArray[14][7]=0;
			this.m_CollisionArray[13][7]=0;
			this.m_WallLayout[13][7]=8;
			this.m_WallLayout[14][7]=8;
			this.m_FloorLayout[14][7]=1;
			this.m_FloorLayout[13][7]=1;
		}
	}
	return 0;
}
